import React, { Component } from 'react';
import Card from "./components/Card";
import chars from "./cards.json";
import Wrapper from "./components/Wrapper";
import Score from "./components/Score";
import './App.css';


class App extends Component {
  // Setting this.state.pups to the cards json array
  state = {
    chars,
    clickedChars: [],
    score: 0,
    goal: 10,
    status:""

  };

  //shuffle the pup cards in the browser when clicked
  shuffleScoreCard = id => {
    let clickedChars = this.state.clickedChars;

    if(clickedChars.includes(id)){
      this.setState({ clickedChars: [], score: 0, status:  "Game Over! You lost. Click to play again!" });
      return;
    }else{
      clickedChars.push(id)

      if(clickedChars.length === 8){
        this.setState({score: 8, status: "You Won! Great Job, Smartie! Click to play again!", clickedChars: []});
        console.log('You Win');
        return;
      }

      this.setState({ chars, clickedChars, score: clickedChars.length, status: " " });

      for (let i = chars.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [chars[i], chars[j]] = [chars[j], chars[i]];
      }
    }
  }













 // Map over this.state.cards and render a Card component for each card object
 render() {
  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">The Clickster</h1>
        <p className="App-intro">
          Try not to click the same image twice!
        </p>
      </header>
      <Score total={this.state.score}
             goal={8}
             status={this.state.status}
             />
      <Wrapper>
        {this.state.pups.map(puppy => (
          <Card
            shuffleScoreCard={this.shuffleScoreCard}
            id={puppy.id}
            key={puppy.id}
            image={puppy.image}
          />
        ))}
      </Wrapper>
      <footer>
        <p>Designed and built by Heather Mathies. You can find the
        code<a href="https://github.com/hmathies/Clicky-Game" target="_blank" rel="noopener noreferrer"> here</a>.</p>
      </footer>
  </div>
  );
}
}

export default App;
